mapper.select:
SELECT COUNT(*) 
FROM FUN_MEMBER 
WHERE ID = #{id}

SELECT COUNT(*) 
FROM FUN_MEMBER 
WHERE EMAIL = #{email}

SELECT * 
FROM FUN_MEMBER 
			
	 
		WHERE ID = #{id} AND PWD = #{pwd}
	
	 
		WHERE ID = #{id}

mapper.update:

UPDATE FUN_MEMBER
SET NICKNAME = #{nickname}, PHONE = #{phone}, EMAIL = #{email}, INFO = #{info}, POSTCODE = #{postcode}, ROADADDRESS=#{roadaddress},DETAILADDRESS=#{detailaddress}, PROFILE = #{profile}	
  
	
	, PWD = #{pwd}		
	
WHERE ID = #{id}
